sources.scss/paint.scss:
// @import "reset.css";
.paint {
  // border: 1px solid red;

  .center {
    top: 90px !important;
    width: 100%;
  }
}

.canvas {
  width: 450px;
  height: 450px;
  background-color: #fff;
  border-radius: 10px;
  box-shadow: 0 4px 6px rgba(50, 50, 93, 0.11), 0 1px 3px rgba(0, 0, 93, 0.11);
}

.controls {
  margin-top: 20px;
  display: flex;
  flex-direction: column;
  align-items: center;

  .flex {
    display: flex;
    justify-content: center;
    width: 100%;
  }

  .controls__range {
    margin-top: 15px;
    margin-right: 20px;
  }

  .controls__range,
  .controls__btns {
    margin-bottom: 20px;

    button {
      border: none;
      background-color: #fff;
      padding: 10px 20px;
      border-radius: 8px;
      margin: 0 5px;
      box-shadow: 0 4px 6px rgba(50, 50, 93, 0.11), 0 1px 3px rgba(0, 0, 93, 0.11);
      font-size: 1rem;
      transition: .2s;
      cursor: pointer;
      text-transform: uppercase;
      font-weight: 600;
      color: #333;
    }
  }

  .controls__colors {
    display: flex;

    .controls__color {
      width: 40px;
      height: 40px;
      box-shadow: 0 4px 6px rgba(50, 50, 93, 0.11), 0 1px 3px rgba(0, 0, 93, 0.11);
      border-radius: 50%;
      margin: 0 3px;
      cursor: pointer;
      transition: .1s;

      &:active {
        transform: translateY(3px);
        box-shadow: 0 0px 2px rgba(50, 50, 93, 0.11), 0 1px 0px rgba(0, 0, 93, 0.11);
      }
    }
  }
}
